status-duration.map:
function (doc) {

  for(s = 0; s < doc.data_obj.order_status.length; s++) {
    var duration = doc.data_obj.order_status[s].timestamp - doc.data_obj.timestamp;
    emit(doc.data_obj.order_status[s].name, duration/60);
  }
}

status-duration.reduce:
function (keys, values, rereduce) {
    avg = Math.round(sum(values)/values.length);
    return avg;
}
